import React, { useState } from 'react';
import './Main.css'; // CSS import
import axios from 'axios';

function Main() {
  const [total, setTotal] = useState({
    schoolname : '',
    address : '',
    latitude : '',
    longitude : '',
  });

  const tms = (q)=>{
   setTotal({...total,[q.target.name] : q.target.value})
  }
  const submit = async(e) => {    
    e.preventDefault();
    const st = await axios.post('http://localhost:5000/plus',total)
    console.log(st);
        // console.log(st);
        // if(st.data.token){
        //     localStorage.setItem('token',st.data.token);
        // }


    
    
  };

  const submit2 = async(e) => {    
    e.preventDefault();

    const token = localStorage.getItem('token');

    const st = await axios.get('http://localhost:5000/',{
        headers: {
        'Authorization': `Bearer ${token}`
      }
    })
    
    console.log(st);
    
  };

  return (
    <div className="login-container">
      <form className="login-form">
        <h2 className="login-heading">Login</h2>
        <input
          type="text"
          placeholder="schoolname..."
          name='schoolname'
          onChange={tms}
          className="login-input"
        />

         <input
          type="text"
          placeholder="address..."
          name='address'
          onChange={tms}
          className="login-input"
        />

         <input
          type="text"
          placeholder="Email"
          name='latitude'
          onChange={tms}
          className="login-input"
        />
        <input
          type="text"
          placeholder="Password"
          name='longitude'
          onChange={tms}
          className="login-input"
        />
        <button type="submit" className="login-button" onClick={submit}>Login</button>
                <button type="submit" className="login-button" onClick={submit2}>Login2</button>

      </form>
    </div>
  );
}

export default Main;
