import React, { useState } from 'react';
import './Rs.css';

function Rs() {
  const [form, setForm] = useState({
    nam: '',
    email: '',
    pass: ''
  });

  const handleChange = async(e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
        const st = await axios.post('http://localhost:5000/login',form)

  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(form);
    alert('Registration data submitted!');
    // yahan backend se connect kar sakte ho
  };

  return (
    <div className="register-container">
      <form className="register-form">
        <h2>Register</h2>
        <input
          type="text"
          name="nam"
          placeholder="Full Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="pass"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          required
        />
        <button type="submit" onClick={handleSubmit}>Register</button>
      </form>
    </div>
  );
}

export default Rs;
