import React, { useEffect, useState } from "react";
import axios from "axios";
import "../Css/BlogEditor.css"; // ‚úÖ Import CSS

const BlogEditor = () => {
  const [blog, setBlog] = useState({
    title: "",
    content: "",
    status: "draft"
  });

  const [blogId, setBlogId] = useState(null);

  useEffect(() => {
    const interval = setInterval(() => {
      autoSave();
    }, 5000);

    return () => clearInterval(interval);
  });
        let count = 0;
  const autoSave = async () => {
    count = count + 1;
    try {
      const res = await axios.post("http://localhost:5000", {
        ...blog,
        _id: count,
        status: "draft"
      });
      setBlogId(res.data._id);
      console.log("Auto-saved:", new Date().toLocaleTimeString());
    } catch (err) {
      console.error("Auto-save error:", err.message);
    }
  };

  const publishBlog = async () => {
    try {
      const res = await axios.post("http://localhost:5000", {
        ...blog,
        _id: blogId,
        status: "published"
      });
      alert("Blog published successfully!");
    } catch (err) {
      alert("Error while publishing");
    }
  };

  return (
    <div className="blog-editor-container">
      <h2>üìù Blog Editor</h2>

      <input
        type="text"
        placeholder="Blog Title"
        value={blog.title}
        onChange={(e) => setBlog({ ...blog, title: e.target.value })}
        className="blog-editor-input"
      />

      <textarea
        placeholder="Write your blog content here..."
        value={blog.content}
        onChange={(e) => setBlog({ ...blog, content: e.target.value })}
        className="blog-editor-textarea"
      ></textarea>

      <div>
        <button className="blog-editor-button" onClick={publishBlog}>
          üöÄ Publish
        </button>
      </div>
    </div>
  );
};

export default BlogEditor;
