import React, { useEffect, useState } from 'react';

import './App.css';
import {io} from 'socket.io-client'

const Chats = () => {
  let hr = new Date().getHours();
  let min = new Date().getMinutes();
  let time = `${hr} : ${min}`;


  const[inputVal,setInputVal] = useState()
  const [messages, setMessages] = useState([]);
  const[totalVal,setTotalVal] = useState({
   
  })

  let obj = {
    from : 'me',
    messages : inputVal,
    time : time,
  }
  

  const am = async() =>{
    setTotalVal(prev => ({
      ...prev,}));
    

    
  const socket = io('http://localhost:5000')

     socket.emit('message2',{
        from : 'me',
       messages : inputVal,
       time : time,
    });
    return () => {
      socket.off('message');
    };
    
    
  }


  useEffect(() => {
    // Receive messages from backend
    const socket = io('http://localhost:5000')

    socket.on('messageFromchat1', (data) => {
      setTotalVal(prev => [...totalVal, data]);
    });

    // Cleanup
    return () => {
      socket.off('messageFromServer');
    };

    
  }, []);
 
  console.log(totalVal);
  

  return (
    <div className={`chat-container`}>
      <div className="chat-header">Chat Application 2</div>
      <div className="chat-body">
  {totalVal.messages.map((msg, i) => (
    <div
      key={i}
      style={{
        display: 'flex',
        justifyContent: msg.from === 'me' ? 'flex-end' : 'flex-start',
        marginBottom: '10px'
      }}
    >
      <div>
        <div
          className="bubble"
          style={{
            backgroundColor: msg.from === 'me' ? '#6cc24a' : '#e0e0e0',
            color: msg.from === 'me' ? 'white' : 'black',
            borderRadius: '15px',
            padding: '10px 15px',
            maxWidth: '60%',
            textAlign: 'left'
          }}
        >
          {msg.text}
        </div>
        <div
          className="timestamp"
          style={{
            fontSize: '12px',
            color: '#555',
            textAlign: msg.from === 'me' ? 'right' : 'left',
            paddingTop: '2px'
          }}
        >
          {msg.time} PM
        </div>
      </div>
    </div>
  ))}
</div>

      <div className="chat-input">
        <input placeholder="Type something..." onChange={(q)=>{setInputVal(q.target.value)}} />
        <button onClick={am}>âž¤</button>
      </div>
    </div>
  );
};



export default Chats;
